void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Fetch the color from Buffer A (iChannel0)
    vec4 color = texture(iChannel0, uv);

    // Output the color to the screen
    fragColor = color;
}

// Define a uniform float for opacity control (ranging from 0 to 1)
uniform float opacity;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Fetch the color from Buffer B (iChannel1)
    vec4 colorB = texture(iChannel1, uv);

    // Blend the colors based on opacity control
    vec4 blendedColor = vec4(colorB.rgb, opacity * colorB.a)*0.5; //opacity multiplier

    // Output the blended color to the screen
    fragColor = blendedColor;
}

