void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = 1.0 / iResolution.xy;

    vec2 uv = fragCoord * scale;


    vec2 uv_0 = uv / AMOUNT;
    vec2 scale_0 = scale;

    vec3 col_0 = vec3(0.0);
    col_0 += texture(iChannel0, uv_0 + vec2(1.0, 1.0) * scale_0).rgb;
    col_0 += texture(iChannel0, uv_0 + vec2(1.0, -1.0) * scale_0).rgb;
    col_0 += texture(iChannel0, uv_0 + vec2(-1.0, 1.0) * scale_0).rgb;
    col_0 += texture(iChannel0, uv_0 + vec2(-1.0, -1.0) * scale_0).rgb;
    col_0 /= 4.0;

    vec2 uv_1 = uv / AMOUNT / AMOUNT;
    vec2 scale_1 = scale / AMOUNT / AMOUNT;

    vec3 col_1 = vec3(0.0);
    col_1 += texture(iChannel1, uv_1 + vec2(1.0, 1.0) * scale_1).rgb;
    col_1 += texture(iChannel1, uv_1 + vec2(1.0, -1.0) * scale_1).rgb;
    col_1 += texture(iChannel1, uv_1 + vec2(-1.0, 1.0) * scale_1).rgb;
    col_1 += texture(iChannel1, uv_1 + vec2(-1.0, -1.0) * scale_1).rgb;
    col_1 /= 4.0;


    vec3 col = mix(col_0, col_1, 0.5);

    fragColor = vec4(col, 1.0);
}
