//philip.bertani@gmail.com

vec3 f1,f2;
float focus = 0.,focus2 = 0.;
#define pi  3.14159265

float random(vec2 p) {
    //a random modification of the one and only random() func
    return fract( sin( dot( p, vec2(12., 90.)))* 1e6 );
}


float noise(vec3 p) {
    vec2 i = floor(p.yz);
    vec2 f = fract(p.yz);
    float a = random(i + vec2(0.,0.));
    float b = random(i + vec2(1.,0.));
    float c = random(i + vec2(0.,1.));
    float d = random(i + vec2(1.,1.));
    vec2 u = f*f*(3.-2.*f);

    return mix(a,b,u.x) + (c-a)*u.y*(1.-u.x) + (d-b)*u.x*u.y;

}

float fbm3d(vec3 p) {
    float v = 0.;
    float a = .5;
    float tt= iTime/60.;
    vec3 shift = .1*vec3( cos(tt)*f1 - sin(tt)*f2);  //play with this

    float angle = pi/7. ;
    float cc=cos(angle), ss=sin(angle);  
    mat3 rot = mat3( cc,  0., ss, 
                      0., 1., 0.,
                     -ss, 0., cc );

    for (float i=0.; i<4.; i++) {
        v += a * noise(p);
        p = rot * p * 2. + shift;
        a *= .6*(1.+ 4.*(focus+focus2));  //changed from the usual .5
    }
    return v;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float coord_scale = 1.;
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mm = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    if ( iMouse.w == 0. ) mm.xy = vec2(1,.4);

    uv *= coord_scale;
    mm *= coord_scale;

    vec3 rd = normalize( vec3(uv, -2.) );  
    vec3 ro = vec3(0);

    ro.z -= iTime*.4;

    vec3 q;    

    float i=0., stepsize=1.;
    vec3 cc=vec3(0);
    vec3 p=ro;
    for (; i<20.; i++) {

        p += rd * stepsize;

        f1 = p-vec3(8.*mm.x,6.*mm.y,ro.z-20.);
        f2 = p-vec3(-8.*mm.x,-6.*mm.y,ro.z-20.);
        focus = length(f1);
        focus = exp(-focus/7.);

        focus2 = length(f2);
        focus2 = exp(-focus2/7.);


        q.x = fbm3d(p);
        q.y = fbm3d(p.yzx);
        q.z = fbm3d(p.zxy);

        float f = fbm3d(p + q);

        cc += q * f * exp(-i*i*1000.);
    }

    cc.r += 3.*focus*focus; cc.g+= 2.*focus; cc.b += 2.5*focus2; cc.r +=3.*focus2;    
    cc /= 2.;

    fragColor = vec4( pow(cc,vec3(3.)),1.0);


}
