// raspberry pi 3 boot screen
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float red = 1.-sqrt(pow(uv.x, 2.) + pow(1.-uv.y, 2.));
    float yel = 1.-sqrt(pow(1.-uv.x, 2.) + pow(1.-uv.y, 2.));
    float blu = 1.-sqrt(pow(1.-uv.x, 2.) + pow(uv.y, 2.));
    float dbl = 1.-sqrt(pow(uv.x, 2.) + pow(uv.y, 2.));

    vec3 col = vec3(1., 0, 0) * red + vec3(1., 1., 0) * yel + vec3(0, 1., 1.) * blu + vec3(0, 0, 1.) * dbl;
    fragColor = vec4(col,1.0);
}
