#define R3D(a) mix(q*dot(p,q),p,cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-2,0,3),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*8.;
    p.z-=2.;
    p=R3D(t);

    for(j=0.;j++<9.;)
        p=abs(p*smoothstep(-1.,1.,p*p/i)-.1)-.095,
        d=max(min(p.z,p.y)/d,p.x/d),
        d-=dot(p,sin(q))/1e4;

    e-=d;

}o+=vec4(exp(p/e)/i,1)*3.;
}
