#define VERIFY_INTERIOR /* thanks to spalmer - detects broken SDFs (can cause more false alarms) */
#define VERIFY_EXTERIOR /* thanks to spalmer - detects broken SDFs (can cause more false alarms) */
// (See e.g. https://www.shadertoy.com/view/ddXGDr verify SDF error detection for test on deliberately incorrect SDF)
// #define VERIFY_GRADIENT_CONTINUITY_ALONG /* WARNING: detects change of direction along gradients but causes false alarms e.g. outwards from box vertices */
#define VERIFY_GRADIENT_CONTINUITY_SIDEWAYS /* WARNING: detects change of direction in "parallel" gradients but causes false alarms where gradients curve (e.g. towards sphere center) or at reflections/symmetries */

#define AA 1

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 500u

#define pi 3.1415926

float slice_z()
{
    //return cos(iTime*0.5)*sqrt(0.5)-sqrt(0.5);
    return -cos(iTime*0.5)*sqrt(2.0);
}

float frame(vec3 p, vec3 b, float e) // https://iquilezles.org/articles/distfunctions/
{
         p = abs(p  )-b;
    vec3 q = abs(p+e)-e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// https://mathworld.wolfram.com/RodriguesRotationFormula.html
// https://www.euclideanspace.com/maths/geometry/rotations/conversions/angleToMatrix/index.htm
mat3 rotation(vec2 cs, vec3 k) // https://www.shadertoy.com/view/mt3BRB rotate from vector to vector, by rodrigues' rotation (jt)
{
    cs = normalize(cs);
    k = normalize(k);
    float t = 1.0 - cs.x;
    return
        mat3
        (
            vec3(t*k.x*k.x+cs.x,t*k.x*k.y+k.z*cs.y,t*k.x*k.z-k.y*cs.y),
            vec3(t*k.x*k.y-k.z*cs.y,t*k.y*k.y+cs.x,t*k.y*k.z+k.x*cs.y),
            vec3(t*k.x*k.z+k.y*cs.y,t*k.y*k.z-k.x*cs.y,t*k.z*k.z+cs.x)
        );
}

mat3 rotation(vec3 src, vec3 dst) // https://www.shadertoy.com/view/mt3BRB rotate from vector to vector, by rodrigues' rotation (jt)
{
    src = normalize(src);
    dst = normalize(dst);
    float d = dot(src, dst);
    vec3 k = cross(src, dst);
    return rotation(vec2(d, length(k)), k);
}

float unit_cube_and_sphere(vec3 p) // https://www.shadertoy.com/view/lXXyD4 unit cube intersect sphere exact (jt)
{
    // use symmetries (mirror)
    p = abs(p);
    p.xy = p.x > p.y ? p.xy : p.yx;
    //p.xz = p.x > p.z ? p.xz : p.zx;
    return
        length(p.yz) > p.x // inside or outside cone?
        ?
        abs((length(p)-sqrt(2.0))) // sphere
        :
        length(vec2(length(p.yz)-1.0, p.x-1.0)); // ring
}

float unit_cube_and_torus(vec3 p) // https://www.shadertoy.com/view/M3XyWN unit cube intersects torus exact, 2024 by jt
{
    // use symmetries (mirror)
    p.x = abs(p.x);
    return
        length(p.yz) > 2.0-p.x // inside or outside cone?
        ?
        length(vec2(length(p.yz)-1.0, 1.0-p.x)) // ring
        :
        abs((length(vec2(p.x,length(p.yz)-2.0))-sqrt(2.0))); // torus
}

float map(vec3 p) // PLACE SDF TO DEBUG HERE
{
    p.xy = fract(p.xy/4.0)*4.0-2.0;
    p.xy = abs(p.xy);
    return
        min
        (
        min(
            unit_cube_and_sphere(p),
            p.z < 0.0
            ?
            abs(length(p-vec3(2,2,0))-sqrt(2.0)) // sphere
            :
            length(vec2(length(p.xy-2.0)-sqrt(2.0), p.z)) // ring
            ),
            min
            (
                unit_cube_and_torus(p-vec3(2,0,0)),
                unit_cube_and_torus(p.yxz-vec3(2,0,0))
            )
        )
        -
        0.01;
}

vec3 gradient(vec3 p) // NOT normalized so we can use it's length to detect errors in the SDF.
{
    vec2 h = vec2(EPSILON,0);
    return (vec3(map(p+h.xyy) - map(p-h.xyy),
                 map(p+h.yxy) - map(p-h.yxy),
                 map(p+h.yyx) - map(p-h.yyx)))/EPSILON/2.0;
}

float trace(vec3 ro, vec3 rd) // iteration count limited to avoid stalling if ray passes very close to surface
{
    float t;
    uint i;
    for(i = 0u, t = 0.0; i < ITER_MAX && t < DIST_MAX; i++)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return DIST_MAX;
}

vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}
/*
// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2
// implementing Chris Wellons' https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}
*/
vec4 trace_slice(vec3 ro, vec3 rd)
{
    vec3 planecolor = vec3(0);
    // z = ro.z+d*rd.z
    // z-ro.z = d*rd.z
    // (z-ro.z)/rd.z = d
    float dist = (slice_z()-ro.z)/rd.z;
    if(dist > 0.0)
    {
        vec3 dst = ro + rd * dist;
        float d = map(dst);
        float n = length(gradient(dst));

        d /= 5.0; // SCALE SDF VISUALIZATION

        // iq's sdf visualization colors
        planecolor = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7);
        planecolor *= 1.0 - exp(-24.0 * abs(d));
        planecolor *= 0.8 + 0.2 * cos(240.0*d);
        planecolor = mix(planecolor, vec3(1.0), 1.0 - smoothstep(0.0, 0.01, abs(d)));

        planecolor = mix(planecolor, vec3(0,1,0), smoothstep(1.0,1.0-EPSILON, n)); // gradient too small? GREEN
        planecolor = mix(planecolor, vec3(1,0,0), smoothstep(1.0,1.0+EPSILON, n)); // gradient too large? RED
        //planecolor = mix(planecolor, vec3(0,0,1), smoothstep(0.0+EPSILON,0.0, n)); // gradient zero? BLUE

        // The error detection is basically, quoting spalmer,
        // "one iteration of snap-point-to-(nearest-)surface algorithm".
        // Jump the distance given by the SDF at the current position
        // along the gradient given by the SDF at the current position
        // (direction to the nearest surface) and evaluate the SDF there.
        // Because the SDF is supposed to describe the distance to the surface,
        // the result should be (close to) zero.
        //float error = map(dst - gradient(dst) * map(dst)); // SDF error detection thanks to spalmer!
        float distance_towards_nearest_surface = map(dst);
        vec3 direction_towards_nearest_surface = gradient(dst);
        float error = map(dst - direction_towards_nearest_surface * distance_towards_nearest_surface);
#ifdef VERIFY_INTERIOR
        planecolor += smoothstep(0.0, sqrt(EPSILON),-error) * (cos(iTime*2.0*pi)*0.5+0.5); // SDF interior otherwise broken? WHITE, BLINKING (can cause false alarms)
#endif
#ifdef VERIFY_EXTERIOR
        planecolor += smoothstep(0.0, sqrt(EPSILON),+error) * (cos(iTime*2.0*pi)*0.5+0.5); // SDF exterior otherwise broken? WHITE, BLINKING (can cause false alarms)
#endif
        //planecolor += smoothstep(0.0,sqrt(EPSILON), abs(error)) * (cos(iTime*2.0*pi)*0.5+0.5); // SDF otherwise broken? WHITE, BLINKING
#ifdef VERIFY_GRADIENT_CONTINUITY_ALONG
        vec3 cross_surface_direction = gradient(dst - direction_towards_nearest_surface * (distance_towards_nearest_surface + sign(distance_towards_nearest_surface) * EPSILON));
        float dir_error = dot(gradient(dst), cross_surface_direction);
        planecolor += smoothstep(0.0,sqrt(EPSILON), abs(dir_error - 1.0)) * (cos(iTime*2.0*pi)*0.5+0.5); // SDF otherwise broken? WHITE, BLINKING
#endif
#ifdef VERIFY_GRADIENT_CONTINUITY_SIDEWAYS
        //vec3 epsilon = (hash33(dst*100.0) * 2.0 - 1.0) * 0.1/*step-size*/;
        vec3 epsilon = (hash33(dst*100.0) * 2.0 - 1.0) * 0.01/*step-size*/;
        //if(abs(dot(gradient(dst-epsilon), gradient(dst+epsilon)) - 1.0) > 0.5/*sensitivity*/)
        if(abs(dot(gradient(dst-epsilon), gradient(dst+epsilon)) - 1.0) > 0.05/*sensitivity*/)
            //planecolor.z += 1.0;
            planecolor += mix(vec3(1,0,1),vec3(0,1,1), 0.5 + 0.5 * cos(iTime * 5.0));
#endif
    }

    return vec4(planecolor, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 o = ivec2(0);
    vec3 sum = vec3(0);
    for(o.y = 0; o.y < AA; o.y++)
    {
        for(o.x = 0; o.x < AA; o.x++)
        {
            vec2 uv = (fragCoord.xy + vec2(o) - vec2(AA-1)*0.5) / iResolution.xy;
            vec2 ndc = 2.0 * uv - 1.0;
            ndc.x *= float(iResolution.x) / float(iResolution.y);
            float mx = 2.0 * pi * float(iMouse.x) / float(iResolution.x);
            float my = pi - pi / 2.0 * float(iMouse.y) / float(iResolution.y);
            mx = (iMouse.x > 10.0) ? mx : pi*0.5+2.0 * pi * fract(iTime * 0.05);
            my = (iMouse.y > 10.0) ? my : 1.25 * pi / 2.0;

            mat2 yaw = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
            vec3 ro = vec3(0.0, 0.5, -7.5 );//vec3(0.0, -10.0 * my, 0.0);
            //mat2 Pitch = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
            mat2 pitch = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
            ro.yz = pitch * ro.yz;
            ro.xy = yaw * ro.xy;

            vec3 rd = normalize(vec3(0.5 * ndc.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
            rd.yz = pitch * rd.yz;
            rd.xy = yaw * rd.xy;

            vec4 slice = trace_slice(ro, rd);
            vec3 scenecolor = vec3(0);

            float dist = trace(ro, rd);
            if(dist < DIST_MAX)
            {
                if(slice.w < 0.0)
                    slice.w = DIST_MAX;
                bool above = dist < slice.w;
                if(above) // view only part above plane (comment-out to view whole shape)
                {
                    vec3 dst = ro + rd * dist;
                    //vec3 dst = ro + rd * (dist - 0.005); // https://www.shadertoy.com/view/dldfW8 "normal back-off" method by elenzil
                    vec3 n = normalize(gradient(dst)); // NOTE: gradient is unnormalized in case of errors!

                    //vec3 lightdir = normalize(vec3(1, 1, 1));
                    vec3 lightdir = normalize(vec3(0, 0, 1));
                    vec3 ambient = vec3(0.1);
                    float diffuse = max(dot(lightdir, n), 0.0);
                    float specular = pow(max(dot(normalize(lightdir - rd), n), 0.0), 50.0);

                    scenecolor = vec3(.1); // object color
                    scenecolor = /*specular+*/ (ambient + diffuse) * scenecolor;

                    {
                        // transform normal to local coordinates
                        n.xy = n.xy*yaw;
                        n.yz = n.yz*pitch;
                        scenecolor = 0.1*max(scenecolor, smoothstep(0.8, 1.0, length(n.xy))); // rim-light
                    }

                    scenecolor = sqrt(scenecolor); // approximate gamma
                }
            }

            vec3 planecolor = 1.5*slice.xyz*exp2(-slice.w*slice.w/20.0); // workaround: "fog" hides moiree pattern

            vec3 color = scenecolor + planecolor; // TRANSPARENT
            //vec3 color = mix(scenecolor, planecolor, step(slice.w, dist)); // SOLID
            sum += color;
        }
    }

    fragColor = vec4(sum/float(AA*AA), 1.0);
}
