#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(1,2,-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e-.1)*8.;
    p.yz*=R2D(t);

    for(j=0.;j++<8.;)
        p=abs(p*p*cross(q,p)*fwidth(p)),
        d-=dot(p.yz,q.xy)/1e4;

    e+=d;

}o+=vec4(log(p),1)*3./i;
}
