const float PI = 3.1416;
const float DIST = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 noiseSample1 = texture(iChannel1, uv + vec2(iTime * 0.1, 0.0));
    vec4 noiseSample2 = texture(iChannel2, (uv * 0.1) + vec2(iTime * 0.02, 0.0));
    vec4 total = vec4(0.0);
    float creaseMask = (1.0 - texture(iChannel0, uv).r);
    float radius = DIST * noiseSample1.r * noiseSample2.r * creaseMask * creaseMask * creaseMask;

    for (int i = 0; i < 16; i++){
        float rad = (PI * float(i)) / 8.0;
        vec2 samplePoint = vec2(cos(rad) * radius, sin(rad) * radius);
        vec4 texSample = texture(iChannel0, uv + samplePoint);
        total += texture(iChannel0, uv + samplePoint);
    }

    total /= 16.0;

    // Output to screen
    fragColor = total;
}
