void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (mod(float(iFrame), float(FRAMESKIP * 2 + 1)) != 0.0) discard;

    vec3 current = texture(iChannel0, fragCoord/iResolution.xy).xyz;
    vec3 previous = texture(iChannel1, fragCoord/iResolution.xy).xyz;


    vec3 difference = current - previous;

    vec3 color = difference * 1.+ 0.5;

    // Output to screen
    fragColor = vec4(color,1.0);
}

