vec2 z1(vec2 z, vec2 c)
{
    float real = z.x*z.x - z.y * z.y;
    float imag = 2.0 * z.x * z.y;
    return vec2(real, imag) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = 5.0 * vec2((fragCoord.x - iResolution.x/2.0)/iResolution.x, (fragCoord.y - iResolution.y/2.0)/iResolution.x);
    int i = 0;
    while(length(z) < 2.0 && i < 50)
    {
        z = z1(z, vec2(sin(iTime/10.0) * cos(iTime/10.0), sin(iTime/10.0) * cos(iTime/10.0)));
        i++;
    }

    vec3 color = vec3(float(i)/50.0);
    fragColor = vec4(color,1.0);
}
