//#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

mat3 R3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}


void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*8.;
    p.z-=2.;
    p*=R3D(t*.5,q);

    for(j=0.;j++<4.;)
        //q=q*q/i;
        //p*=R3D(t*.5);
        p=abs((p+q)*R3D(t*.5,q*q/i)-.01)-.04,
        d=min(max(p.x,p.y),p.z),
        d-=dot(p,q)/2e4;

    e-=d*log(d);

}o+=vec4(exp(p/d),1)*3./i;
}
