void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float pos = iTime + uv.x * 15.0 * uv.y;

    float waveOne = 0.5 + 0.25 * sin(pos);
    float waveTwo = 0.25 + 0.1 * abs(cos(pos));
    float waveThree = clamp(0.25 + 0.1 * tan(pos), 0.4, 0.6);

    vec3 col = vec3(0.8,0.8,1.0) * 10.0 * abs((1.0 - clamp(abs(uv.y - (waveOne + waveTwo + waveThree) * 0.25), 0.0, 0.1) - 0.9));

    // Output to screen
    fragColor = vec4(col,1.0);
}
