Main {

    Q = A(U);

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec4 m = 0.25*(n+e+s+w);
    Q.z = mix(Q.z,m.z,.0)+.5*(e.x-w.x-s.y+n.y);

}

Main {
    Q = vec4(0);
    for (float x = -2.; x<= 2.; x++) 
    for (float y = -2.; y<= 2.; y++) 
    {
        vec2 u = vec2(x,y);
        Q += 0.3989422804*A(U+u)*exp(-.5*dot(u,u));
    }
}

Main {
    vec2 u = U;
    #define dt .06
    if (iFrame%1==0) {
        for (int i = 0; i < 10; i++) {
            vec4 v = B(U);
            U -= dt*v.xy;
        }
    }
    Q = A(U);
}
