void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1.0);

    vec3 p = vec3(uv.x,
                  uv.y-0.5-1.0,
                  abs(uv.y-0.5)
                  );
    vec2 pos = vec2(sin(iTime*1.325),cos(iTime*1.853));
    vec2 proj = vec2(p.x/p.z,p.y/p.z)+pos;

    col = texture(iChannel0,proj).xyz;


    vec2 ku = (uv - vec2(0.25,0.25));;
    ku /= abs(sin(iTime+2.5));

    ku.x -= sin(iTime);
    ku = clamp(ku,0.0,1.0);
    ku.x *= 0.15;

    vec4 kitty = texture(iChannel1,ku);

    float shit_shading = clamp(3.*(abs(p.z)/0.5),0.2,0.8);

    col *= shit_shading;

    col = mix(col,kitty.xyz,kitty.a);

    fragColor = vec4(col,1.0);
}
