#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p=vec3(log2(length(p)),exp2(p.y)-tan(iTime*.2),atan(p.x,p.z));
    p.xy*=R2D(iTime*.2);

    for(j=0.;j++<8.;)
        p=abs(abs(p)-.5)-.2,
        d=pow(length(p.yz),4.),
        d-=dot(cos(p),sin(p/length(p)-.5));
    e+=d;
}o+=vec4(-log(p+p),1)*3./i;
}
