vec2 trans(vec2 pos){
    vec2 uni = (2.*pos - iResolution.xy)/iResolution.y;
    return uni;
}

float lightLine(vec2 a, vec2 b, vec2 p){

    // This is from integrating the function f(t) = 1/length(a*(1-t) + b*t - p)**2 from 0 to 1

    vec2 l = b - a;
    vec2 d = a - p;

    float ll = dot(l, l);
    float ld = dot(l, d);

    float s = -l.y*d.x + l.x*d.y;

    return length(l) / s * (atan((ld+ll) / s) - atan(ld / s));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = trans(fragCoord.xy);
    vec2 oldMouse = trans(abs(iMouse.zw));
    vec2 mouse = trans(iMouse.xy);

    if(iMouse.xy == vec2(0, 0)){
        mouse = (0.8*vec2(iResolution.x/iResolution.y, 1.) + 0.05*sin(iTime*0.5)*vec2(-1., iResolution.x/iResolution.y));
        oldMouse = -(0.8*vec2(iResolution.x/iResolution.y, 1.) + 0.05*sin(iTime*0.5)*vec2(-1., iResolution.x/iResolution.y));
    }

    vec3 light = vec3(0.2, 0.1, 0.8) * lightLine(oldMouse, mouse, p) / 32.;
    vec3 normalised = 1. - 1./(light + 1.);

    fragColor = vec4(normalised, 1.);

}
