float getVal(vec2 uv, float modVal)
{
    int x = int(uv.x);
    int y = int(uv.y);
    int val = x^(x<<y);
    return mod(float(val),modVal)/modVal;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float modVal = floor(iTime*2.0);
    vec2 uv = 50.*(2.*fragCoord - iResolution.xy)/iResolution.y;
    float val = getVal(uv, modVal);
    vec3 col = vec3(val);
    fragColor = vec4(col,1.0);
}
