float HexDist(vec2 p)
{
    //drawing hexagon
    p = abs(p); //clamps where the hexagon is drawn
    float c = dot(p, normalize(vec2(1, 1.73))); //1.73 is sqrt of 3
    c = max(c, p.x);        //idk what this does
    return c;
}


//function that returns hex coordinate
vec4 HexCoords(vec2 uv)
{
    vec2 r = vec2(1, 1.73);
    vec2 h = r / 2.;
    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv;

    if(length(a) < length(b))
    {
        gv = a;
    }
    else
    {
        gv = b;
    }


    float x = atan(gv.x, gv.y);
    float y = .5-HexDist(gv);

    //calculating ID
    vec2 id = uv-gv;

    return vec4(x, y, id);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    uv *= 20.;

    vec4 hc = HexCoords(uv);

    float c = smoothstep(.05, .03, hc.y * sin(hc.z / hc.w + iTime));

    col += c;

    //col.rg = HexCoords(uv).xy;

    //col += sin(HexDist(HexCoords(uv).xy) * 10. + iTime * 10.); //second parameter is size

    // Output to screen
    fragColor = vec4(col,1.0);
}
