vec3 interpolateColor(float interpolationParameter, vec3 colorA, vec3 colorB, vec3 colorC, vec3 colorD) {
    return colorA + colorB * cos(6.28318 * (colorC * interpolationParameter + colorD));
}

void mainImage(out vec4 fragmentColor, in vec2 fragmentCoordinates) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 normalizedTextureCoordinates = (fragmentCoordinates * 2.0 - iResolution.xy) / iResolution.y;
    float distanceFromOriginBeforeTransformation = length(normalizedTextureCoordinates);
    vec3 finalColor = vec3(0.0);

    for(float iterator = 0.0; iterator < 5.0; iterator++) {
        // Add repetitions with fract()
        normalizedTextureCoordinates = fract(normalizedTextureCoordinates * 1.5) - 0.5;

        float distanceFromOrigin = length(normalizedTextureCoordinates);

        vec3 interpolatedColors = interpolateColor(distanceFromOriginBeforeTransformation + iterator * 0.1 + iTime * 0.5,
            vec3(0.764, 0.508, 0.479),
            vec3(0.630, 0.615, 0.608),
            vec3(1.203, 1.459, 0.993),
            vec3(1.974, 3.863, 2.899));

        distanceFromOrigin = sin(distanceFromOrigin * 4.0 - iTime) / 4.0;
        distanceFromOrigin = 0.002 / distanceFromOrigin;
        distanceFromOrigin = smoothstep(0.0, 0.1, distanceFromOrigin);

        finalColor += interpolatedColors * distanceFromOrigin;
    }

    // Output to screen
    fragmentColor = vec4(finalColor, 1.0);
}
