void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 0.5*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float intensity = 0.0;
    float maxI = 10.0;
    float blurRad = 0.05;
    for(float i = 0.0;i < maxI;i++)
    {
        vec2 samplePoint = uv+blurRad * vec2(cos(i),sin(i));
        intensity += texture(iChannel0,samplePoint).x;
    }
    intensity = intensity/maxI;
    fragColor = vec4(intensity);
}
