void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 texel = 1. / iResolution.xy;

    vec2 s;
    vec2 n;
    float coord;
    float frame = float(iFrame);
    float cycle = iResolution.x+iResolution.y;
    float dir = mod(frame,cycle);
    if (dir<iResolution.x){
    s  = vec2(-texel.x,0.0);
    n  = vec2(texel.x,0.0);
    coord = fragCoord.x;}
    else{
    s  = vec2(0.0,-texel.y);
    n  = vec2(0.0,texel.y);
    coord = fragCoord.y;}

    vec4 im_n =  texture(iChannel0, uv+n);
    vec4 im =    texture(iChannel0, uv);
    vec4 im_s =  texture(iChannel0, uv+s);

    float len_n = im_n.b;
    float len = im.b;
    float len_s = im_s.b;
    dir= mod(frame,cycle*3.);
    if (dir<cycle){
    len_n = im_n.r;
    len = im.r;
    len_s = im_s.r;}
    else if (dir<cycle*2.){
    len_n = im_n.g;
    len = im.g;
    len_s = im_s.g;}

    bool A = len_s > len;
    bool B = len_n < len;
    if (mod(iTime,6.*cycle)>cycle*3.){
    A = len_s < len;
    B = len_n > len;}

    if(int(mod(float(iFrame) + coord, 2.0)) == 0) {
        if (A) { 
            im = im_s;    
        }
    } else {
        if (B) { 
            im = im_n;    
        }   
    }

    // initialize with image
    if(iFrame<10) {
        fragColor = texture(iChannel1, uv);
    } else {
        fragColor = im;
    }

}
