#define norm(n) (clamp(n, 0.0, 256.0) / 256.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float d = distance(fragCoord, vec2(iMouse.x, iMouse.y));

    float I = 230.0; // Light intensity
    float k = 1.0; // Falloff constant
    float ll = I - k*d; // Light level
    vec3 cc = vec3(1.0, 0.5, 0.5); // Color coefficients

    // Time varying pixel color
    vec3 col = vec3(norm(ll), norm(ll), norm(ll)) * cc;

    // Output to screen
    fragColor = vec4(col,1.0);
}
