void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord - iResolution.xy * .5;

    float c = .0;
    if(length(uv) <= iResolution.x * 0.2){
      c = 1.0;
    }
    // Output to screen
    fragColor = vec4(vec3(c),1.0);
}
