void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime - 3.0;
    t += 3.0;
    t = mod(t,10.0);
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(uv,1.0); 
    col += iTime*0.5*cos(iTime*uv.xyx+vec3(t,2,4)) + uv.y * 2.;
    col += iTime*0.2*sin(iTime*uv.xyx+vec3(0,t,4)) + 2.;
    col += iTime*0.5*tan(iTime*uv.xyx+vec3(0,2,t)) + uv.y / 2.;
    fragColor = vec4(col,1.0);
}
