#define tau 3.14159 * 2.
#define speed iTime * 1.

// https://gist.github.com/ForeverZer0/f4f3ce84fe8a58d3ab8d16feb73b3509

vec4 hueShift(vec3 col) {
    // 0 - 1 scale + speed
    float hue = (iMouse.x/iResolution.x * tau) + speed;
    const vec3 k = vec3(0.57735);
    float cosAngle = cos(hue);
    vec3 color = col * cosAngle + cross(k, col) * sin(hue) + k * dot(k, col) * (1.0 - cosAngle);
    return vec4(color, 1.);
}

void mainImage( out vec4 O, in vec2 I )
{
	O = hueShift(vec3(1,0,0));
}
