int maxSteps = 13;
vec3 C = vec3(1.499816, 0.31578842, 1.36545851);
float offset = 0.2199;
float kNum = 0.69052;
float kMax = 1.2993499;
float iterOffset = -0.796;
float kOffset = 0.;

vec3 camera = vec3(3.09835829, -1.26052113, -12.84760);
vec3 aim = vec3(0.89711151, 0.19287779, -0.3974);
float ostrength = 0.;
float ocycle = 0.;
vec4 orbit2 = vec4( 0.38347405, 1.86336986, -0.01788503, -2.71105793);
vec4 orbit3 = vec4(0.87534959, 1.04847687, 0.03234359, 0.00505466);
vec3 orbit4 = vec3(-2.19183836, 1.41098694, 1.56200985);
vec3 phongColor =  vec3(0.83808931, 0.81513296, 0.67240545);
vec3 phongPosition = vec3(-14.75765359, 3.38693388, 19.74990);
vec3 phongParam = vec3(0.25791018, 0.56664857, 5.953758);
vec4 spotLightParam = vec4(3.,2.1,-4.,0.09);
vec3 spotLightColor = vec3(0.783,0.422,0.0056);
float fogDistance = -1.;
float fogAmount = 0.12;
float fogColor = 0.4;

vec3 direction;
vec3 normal;
vec3 position;
vec3 color;
float depth;
int iter;
vec4 orbitTrap;

// --------------------------------------------------------

float DE(vec3 p) {
    float iter,k,r2,scale = 1.0;
	vec3 ot,cc = abs(C);
	float angle= 0.0;

	for(int n=0;n<maxSteps;++n) {		
		p =  2.0 * clamp(p, -cc,cc) - p;
		r2 = dot(p,p);		

		iter = float(n) * iterOffset;
		k = max(kNum/r2, (iter + kMax));

		scale *= k;
		k *= (1.0 + kOffset);
		p *= k;

		ot = p - orbit4;
		orbitTrap = min(orbitTrap, vec4(abs(ot), dot(ot,ot)));
	}

	float l = length(p.xy);
	float rxy = l - offset;
	float n = l * p.z;
	rxy = max(rxy, -n / (length(p))-0.02);
	return (rxy) / abs(scale);
}

// --------------------------------------------------------
// https://iquilezles.org/articles/normalsSDF
void calcNormal() {
    float d0 = DE(position);
    const vec2 epsilon = vec2(.001,0);
    vec3 d1 = vec3(
        DE(position-epsilon.xyy),
        DE(position-epsilon.yxy),
        DE(position-epsilon.yyx));
    normal = normalize(d0 - d1);
}

// --------------------------------------------------------

void setDirection(vec2 U) {
    vec3 viewVector = normalize(aim);
    vec3 sideVector = vec3(0.,1.,0.);
    sideVector = normalize(cross(sideVector,viewVector));
    vec3 topVector = cross(viewVector,sideVector);

    vec2 vPos = (U - iResolution.xy * 0.5)/iResolution.y;
    direction = normalize(viewVector + vPos.x * sideVector + vPos.y * topVector);
}

// --------------------------------------------------------

vec3 generateColor(float weight,float x) {
	if(weight == 0.0) return vec3(0.0);

	x = cos(x);
	float r = (1.0-x)*(1.0-x);
	float g = x*x;
	float b = 2.0*(1.0-x)*x;
	return vec3(r,g,b);
}

vec3 orbitTrapCycle(float cycle,vec3 c, float s) {
    float ss = s * cycle;
    return vec3(0.5) + 0.5 * vec3( cos(ss + c.x), cos(ss + c.y), cos(ss + c.z));
}

vec3 getOrbitColor() {
    vec3 orbitColor;

    if (ocycle > 0.0) {
        orbitColor =
        orbitTrapCycle(ocycle,generateColor(orbit2.x,orbit2.y), orbitTrap.x) * orbit2.x * orbitTrap.x +
        orbitTrapCycle(ocycle,generateColor(orbit2.z,orbit2.w), orbitTrap.y) * orbit2.z * orbitTrap.y +
        orbitTrapCycle(ocycle,generateColor(orbit3.x,orbit3.y), orbitTrap.z) * orbit3.x * orbitTrap.z +
        orbitTrapCycle(ocycle,generateColor(orbit3.z,orbit3.w), orbitTrap.w) * orbit3.z * orbitTrap.w;
    } else {
        orbitColor =
        	generateColor(orbit2.x,orbit2.y) * orbit2.x * orbitTrap.x +
        	generateColor(orbit2.z,orbit2.w) * orbit2.z * orbitTrap.y +
        	generateColor(orbit3.x,orbit3.y) * orbit3.x * orbitTrap.z +
        	generateColor(orbit3.z,orbit3.w) * orbit3.z * orbitTrap.w;
    }

    return orbitColor;
}

// --------------------------------------------------------
// phongParam = diffuse,spec,ambient
void phongLight() {
	vec3 L = normalize(phongPosition - position);
	float dotLN = dot(L, normal);
	if (dotLN < 0.) return;

	float t1 = phongParam.x * dotLN;
	vec3 V = normalize(camera - position);
	vec3 R = normalize(reflect(-L, normal));
	float dotRV = dot(R, V);

	if (dotRV < 0.0) {
		color += phongColor * t1;
        return;
    }

	float t2 = phongParam.y * pow(abs(dotRV), phongParam.z);
	color += phongColor * (t1 + t2);
}

// --------------------------------------------------------
// spotLightParam = strength,x,y,exp

const float PI = acos(-1.);

void spotLight() { 
	float a1 = spotLightParam.y * PI;
	float a2 = spotLightParam.z * PI * 0.5;
	float s1 = sin(a1);
	vec3 spotDir = normalize(vec3(s1 * cos(a2), s1 * sin(a2), cos(a1)));
	vec3 halfVector = normalize(spotDir - direction);

	float nDotL = max(0., dot(normal, spotDir));
	float hDotN = max(0., dot(normal, halfVector));

    vec3 c = spotLightColor;// * spotLightParam.x;
    float sexp = spotLightParam.w;

	color += c * ((sexp + 2.) / 2.) * pow(hDotN, sexp) 
		* (sexp + (1. - sexp) * pow(1. - hDotN, 5.)) * nDotL * spotLightParam.x;
}

// --------------------------------------------------------

void ambient() { 
    const float amb = 0.2;
    const float dim = 0.04;
    const float contrast = 1.;
    const float vibrant = 0.01;

    color = vec3(amb) + vec3(1.0 - (normal * vibrant + sqrt(float(iter) * dim)));
    color = vec3(0.5) + (color - vec3(0.5)) * contrast; 

    phongLight();
    spotLight();

    if(ostrength > 0.0) {
        vec3 oColor = getOrbitColor();
        color = mix(color, oColor, ostrength);
    }    
}

// --------------------------------------------------------
const float MIN_DIST = 0.001;
const float MAX_DIST = 12.0;
const float EPSILON = 0.001;

void rayMarch() {
	float distance;
	position = camera;
	iter = 0;
	depth = MIN_DIST;

    orbitTrap = vec4(10000.);

	for(int i = 0;i < 100;++i) {
		distance = DE(position);
        if(abs(distance) < EPSILON || depth > MAX_DIST) break;
        iter += 1;

        depth += distance;
        position += direction * distance;
    }
}

// --------------------------------------------------------

#define animate(v,v1,v2,speed) v = mix(v1,v2,0.5 + sin(iTime * speed) * 0.5)
#define animate2(v,base,delta,speed) v = mix(base-delta,base+delta,0.5 + sin(iTime * speed) * 0.5)

void mainImage(out vec4 O, vec2 U) {

    animate2(C.x,1.5,0.95,  0.07);
    animate2(C.y,0.32,0.18,0.08);
    animate2(C.z,1.35,0.03, 0.09);
    animate2(kNum,0.69,0.05,  0.10);
    animate2(kMax,1.4,0.03, 0.12);
    animate2(offset,2.2,0.03, 0.08);
    animate2(iterOffset,-0.8,0.03, 0.1);

    animate2(spotLightParam.x,2.26,0.5,0.3);
    animate2(spotLightParam.y,2.26,0.5,0.3);
    animate2(spotLightParam.z,-2.,0.5,0.35);
    animate2(spotLightParam.w,1.,0.2,0.4);
    animate2(spotLightColor.x,0.5,0.5,0.1);
    animate2(spotLightColor.y,0.5,0.5,0.2);
    animate2(spotLightColor.z,0.5,0.5,0.4);

    animate2(phongParam.x,0.25,0.05,0.3);
    animate2(phongParam.y,0.5,0.1,0.35);
    animate2(phongParam.z,5.,1.,0.4);

    animate2(phongColor.x,0.5,0.5,0.3);
    animate2(phongColor.y,0.5,0.5,0.7);
    animate2(phongColor.z,0.5,0.5,0.9);
    animate2(phongPosition.x,0.,5.,0.1);
    animate2(phongPosition.y,0.,5.,0.5);
    animate2(phongPosition.z,0.,5.,0.7);

    animate(ostrength,0.,0.3,0.1);
  //  animate(ocycle,0.,3.,0.18);
    animate(orbit2.x,-3.,3.,0.011);  // X wt,color
    animate(orbit2.y,-3.,3.,0.013);
    animate(orbit2.z,-3.,3.,0.015);  // Y wt,color
    animate(orbit2.w,-3.,3.,0.017);
    animate(orbit3.x,-3.,3.,0.019);  // Z wt,color
    animate(orbit3.y,-3.,3.,0.021);
    animate(orbit4.x,-3.,3.,0.23);  // center
    animate(orbit4.y,-3.,3.,0.25);
    animate(orbit4.z,-3.,3.,0.27);
/*
    animate2(camera.x,2.,.5,   0.35);
    animate2(camera.y,-4.,0.5,   0.4);
    animate2(camera.z,-2.,2.,   0.45);
 */   
    animate(aim.x,-3.,3.,   0.01);
    animate(aim.y,-3.,3.,   0.03);
    animate(aim.z,-3.,3.,   0.05);

    color = vec3(0.);
    setDirection(U);
	rayMarch();

	if(depth < MAX_DIST) {
        calcNormal();
        ambient();
    }

    if(fogDistance != 0.0) {
        float f = depth - fogDistance;
        if(f > 0.0) {
            f = min(1.0,f * fogAmount);
            color = mix(color, vec3(0.2 + f * fogColor),f);
        }
    }

    O.xyz = color;
}
