float pi = 3.14159;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ITERATIONS = 300.0;
    float ZOOM = 1.0;
    vec2 COORDS = vec2(0);

    if (iMouse.z>0.0){
    ZOOM =10.0;
