void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //define desired resoltion
    float halftoneres = float(64);
    //pixelate our image. We do this by modifying the UV coordinates before the image is set
    vec2 halftoneuv = vec2((trunc(uv*halftoneres*(iResolution.xy/iResolution.yy))/halftoneres/(iResolution.xy/iResolution.yy)));
    //make our image using the pixelated UV coordinates
    vec3 halftoneimg = vec3(texture(iChannel0, halftoneuv).xyz);


    //comment this line of code to enable color
    //halftoneimg = vec3(((halftoneimg.xx+halftoneimg.yy+halftoneimg.zz)/3.0).xxx); 

    //note that we recycle the halftoneuv vector
    //turn our UV coordinates into a lot of smaller repeating ones
    halftoneuv = vec2(fract(uv*halftoneres*(iResolution.xy/iResolution.yy)));
    //take the distance between our uv coordinate and the point 0.5,0.5, to get a grid of circle gradients.
    halftoneuv = vec2(distance(halftoneuv, vec2(0.5, 0.5))*2.0);
    //check if the color value of the image is more than the threshold set by the circle gradients
    halftoneimg = vec3(greaterThan(halftoneimg.xyz ,halftoneuv.xxx));

    // Output to screen
    fragColor = vec4(halftoneimg.xyz ,1.0);
}
