#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-1,1,-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y*e,e-.2);
    //p*=atan(t,i);
    p.xz*=R2D(t*.2);

    for(j=0.;j++<16.;)
        p=abs(p-.25)-.05,
        d=max(max(p.z,p.x),p.y/d),
        d-=dot(p-q,p/q)/1e4;

    e-=d;

}o+=vec4(exp(p),1)*3./i;
}
