void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the coordinates of the current fragment
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate the time-based offset for horizontal scrolling
    float scrollSpeed = 0.01; // Adjust this value to change the scrolling speed
    float offset = iTime * scrollSpeed;

    // Apply the horizontal scroll
    uv.x += offset;

    // Sample the texture
    vec4 color = texture(iChannel0, uv);

    // Output the color to the fragment
    fragColor = color;
}
