void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized coordinates
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    // Apply matrix transformation (rotation)
    uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * uv;

    // Raymarching setup
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0);
    vec3 rayDirection = normalize(vec3(uv, 1.0));
    float distance = 0.5;
    vec3 color = vec3(0.0);

    // Raymarching iterations
    for (int i = 0; i < 100; i++) {
        vec3 rayPosition = rayOrigin + distance * rayDirection;
        float terrainHeight = sin(rayPosition.x * 6.0) + cos(rayPosition.y * 10.0); // Adjusted terrainHeight
        float sceneDist = rayPosition.y - terrainHeight;

        // Apply color interpolation based on distance
        vec3 interpolatedColor = mix(vec3(0.2, 0.7, 0.9), vec3(1.0, 0.8, 0.2), smoothstep(0.0, 0.1, abs(sceneDist)));

        // Accumulate color based on distance
        color += interpolatedColor * 0.9
        ;

        // Increment distance
        distance += 0.2;

        // Break if close to the surface
        if (abs(sceneDist) < 0.01) {
            break;
        }
    }

    // Apply additional color modulation based on time
    color *= 0.5 + 0.5 * sin(iTime);

    // Output final color
    fragColor = vec4(color, 1.0);
}
