void mainImage( out vec4 color, in vec2 coord )
{
    float n1 = 1.0,
          n2 = 1.5,
          lambda = 0.1,
          t = iTime,
          theta1 = 0.8 * cos(0.15 * t),
          theta2 = asin(n1 / n2 * sin(theta1)),
          cr = (n1 - n2) / (n1 + n2),
          ct = 2.0 * n1 / (n1 + n2);

    vec2 res = iResolution.xy,
         r = (2.0 * coord - res) / res.y,
         ki = n1 / lambda * vec2(cos(theta1), sin(theta1)),
         kr = n1 / lambda * vec2(-cos(theta1), sin(theta1)),
         kt = n2 / lambda * vec2(cos(theta2), sin(theta2));

    float 
          ai = cos(dot(ki, r) - t * 2.0),
          ar = cos(dot(kr, r) - t * 2.0) * cr,
          at = cos(dot(kt, r) - t * 2.0) * ct,
          a = mix(ai + ar, at, step(0.0, r.x));

    vec3 col = 0.5 + 0.5 * cos(t + r.xyx + vec3(0, 2, 4));

    color.xyz = a * a * col;
}
