const vec3 COLOR = vec3(0.9, 0.4, 0.2);

float hash12(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float noise(vec2 p) {
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.-2.*u);
	float res = mix(
		mix(hash12(ip),hash12(ip+vec2(1,0)),u.x),
		mix(hash12(ip+vec2(0,1)),hash12(ip+vec2(1)),u.x),u.y);
	return res*res;	
}

float fbm(vec2 p, int octaves) {
	float s = 0.0, m = 0.0, a = 1.0;
	for(int i = 0; i < octaves; i++) {
		s += a * noise(p);
		m += a;
		a *= 0.6;
		p *= mat2(1.6, 1.2, -1.2, 1.6);
	}
	return s / m;
}

vec2 mercator(vec3 n) {
    return vec2(atan(n.z, n.x) * 0.5, acos(-n.y)) / 3.14159;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res;
    float len = length(uv);
    fragColor = vec4(0);
    if (len > 1.2)
        return;

    vec2 muv = mercator(vec3(uv, -sqrt(0.999 - len * len)));

    const float RADIUS = 0.8;
    float density = exp(-max(0.0, len - RADIUS) * 8.0);
    vec4 atmosphere_col = vec4(COLOR, 1) * density * density * 1.9;
    atmosphere_col *= sqrt(noise(vec2(abs(atan(uv.y, uv.x)) * 12.0))) * 0.4 + 0.6;

    uv /= RADIUS;
    len /= RADIUS;
    muv = mercator(vec3(uv, -sqrt(0.999 - len * len)));

    float surface = smoothstep(-0.3, 1.0, fbm(muv * 32.0, 4));
    vec4 planet = vec4(COLOR * (surface + pow(len, 8.0)), 1) * smoothstep(1.0, 1.0 - fwidth(len) * 4.0, len);
    atmosphere_col = mix(atmosphere_col, planet, planet.a);

    fragColor = planet + atmosphere_col;
}
