renderpass.code:
#define h21(p) fract(sin(dot(p, vec2(12.34,26.534))) * 987.23)
#define r iResolution.xy
#define t iTime
#define pi acos(-1.)
#define TAU (2.* pi)

float truchet(vec2 p, bool coin1, bool coin2){
    p.y *= coin1 ? 1. : -1.;

    float d = smoothstep(.5, .7, 
                  min(
                     length(p + .5), 
                     length(p - .5)
                  )
              );

    return coin1 ^^ coin2 ?  1. - d :  d;
}

float render(vec2 p){
    p = 12. * fract((p - t * .5) / TAU); 

    vec2 q = floor(p) / 2.;
         p = fract(p) - .5;

    bool coin1 = h21(q) < .5,
         coin2 = fract(q.y + q.x) * 2. < .5;

    return smoothstep(30. / r.y, .0, 
               truchet(p, coin1, coin2)
           ) * .75 + .25;
}


void mainImage(out vec4 o, vec2 u) {
    u = (u - r / 2.) / r.y;

    vec2 p = vec2(
                 log(length(u)), 
                 atan(u.y, u.x)
             );

    o = mix(
            render(u), 
            render(p), 
            render(p * 6.) * .5 
                + cos(t)
        ) * vec4(.6);
}
