// Fork of "Mosaic Prism" by Fuergu. https://shadertoy.com/view/l3lfR8
// 2024-10-04 12:49:20


vec2 GetDist(vec3 p) {
    float r = 3.;
    float IOR = 2.1;
    int subsection = 64;
    float thickness = 7.;
    float a = -6.1;
    float Prism1 = nPrism(subsection, vec3(p.x + 5.+a, p.y*.7, p.z), 4., 12.);
    float Prism2 = nPrism(subsection, vec3(p.x - 5.-a, p.z*.7, p.y), 4., 12.);
    float box1 = sdBox(vec3(p.x+a-1., p.y, p.z), vec3(thickness));
    float box2 = sdBox(vec3(p.x-a+.9, p.y, p.z), vec3(thickness));
    float box3 = sdBox(vec3(p.x, p.y, p.z), vec3(5.4));
    float d1 = max(-box1,Prism1);  
    float d2 = max(-box2,Prism2);   
    float d3 = min(d1,d2);

    return vec2(max(box3,d3), IOR);    
}

float RayMarch(vec3 ro, vec3 rd,float side) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p).x*side;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p).x - 
        vec3(GetDist(p-e.xyy).x, GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy+vec2(.5);
    m.x*=1.5;
    m.y*=-1.3;
    //m.x = 200./800.*iResolution.x;
    //m.y = 33./450.*iResolution.y;
    //m.x*=cos(T/10.);m.y=.7;
    vec3 ro = vec3(0., zoom, -5.);
    ro.yz *= Rot(-m.y*PI);
    ro.xz *= Rot(-m.x*TAU);

    //m = iMouse.xy/iResolution.xy;
    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), FOV);
    vec3 col = texture(iChannel0,rd).rgb;

    float d = RayMarch(ro, rd,1.);//raymarch outside the object




    float IOR = 1.5;

    if(d<MAX_DIST) 
    {
        vec3 p = ro + rd * d;// 3d hit position
        vec3 n = GetNormal(p);// normal of surface... orientation
        vec3 r = reflect(rd, n);
        vec3 refOutside = texture(iChannel0,r).rgb;
        IOR = GetDist(p).y;
        vec3 rdIn = refract(rd,n,1./IOR);//ray direction when entering

        //vec3 refrDir = refract(rd,n,1./IOR);//IOR determines how much light is blended

///////////////////////////////////////////////////////////////////

        //fresnel
        vec3 fresnelColor =1.02*vec3(dot(normalize(ro),n));
        vec3 fresnel = vec3(pow(vec3(dot(rd,n))+1.,FresnelReflection));

        vec3 reflTex = vec3(0.);
        vec3 reflTexCurrent = vec3(0.);
        vec3 pEnter = vec3(0.);
        vec3 pExit = vec3(0.);
        vec3 glassColor = vec3(1.);
        float opticalDistence = 0.;
        float opticalDistance = 0.;

        //multiple times reflection inside
        for (int i=1;i<=ReflectTimes;i++)
        {
            //添加内部反射
            pEnter = p-n*SURF_DIST*3.;
            float dInside_current = RayMarch(pEnter, rdIn,-1.);//raymarch inside the object, start from p, direction is refrDir
            pExit = pEnter + rdIn * dInside_current;//3d position of exit 
            vec3 nExit = -GetNormal(pExit);//normal inside is fliped

            //red
            vec3 rdOut = refract(rdIn,nExit,IOR-1.5*Dispersion);
            if(dot(rdOut,rdOut)==0.) rdOut = reflect(rdIn,nExit);//全反射
            reflTexCurrent.r = texture(iChannel0,rdOut).r*glassColor.r;
            //green
            rdOut = refract(rdIn,nExit,IOR);
            if(dot(rdOut,rdOut)==0.) rdOut = reflect(rdIn,nExit);//全反射
            reflTexCurrent.g = texture(iChannel0,rdOut).g*glassColor.g;
            //blue
            rdOut = refract(rdIn,nExit,IOR+Dispersion);
            if(dot(rdOut,rdOut)==0.) rdOut = reflect(rdIn,nExit);//全反射
            reflTexCurrent.b = texture(iChannel0,rdOut).b*glassColor.b;

            //attenuation

            opticalDistance += dInside_current;
            float attenuation = exp(-opticalDistance*Attenuation);
            reflTex += reflTexCurrent*attenuation*pow(.2,float(i-1));



            //output
            //col = rdOut*.5+.5;
            col = reflTex;
            //col = mix(reflTex,refOutside,fresnel);

            //update
            rdIn = rdOut;  pEnter = pExit;
       } 
    }

    col = pow(col, vec3(1.));	// gamma correction

    fragColor = vec4(col,1.0);
}

//ReflectTimes is no need to be greater,as we are not interested in refraction
// interested in the phenomenon of transmitted light
#define ReflectTimes 2

#define Attenuation 0.02
#define Dispersion 0.003
#define FresnelReflection vec3(1.000,1.000,1.000)
const float FOV = 1.;
const float zoom = 20.;
#define MAX_STEPS 1000
#define MAX_DIST 100.
#define SURF_DIST 0.0001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p = mat3(1, 0, 0, 0, 0, -1, 0, 1, 0) * p;
  p.xy*=Rot(15.);
  p.y = abs(p.y);
  p.y+=h/2.;
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float map(float value, float inMin, float inMax, float outMin, float outMax) {
    return (value - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
}

vec3 smoothstep3(vec3 a, vec3 b, float x)
{
    vec3 result;
    result.x = smoothstep(a.x, b.x, x);
    result.y = smoothstep(a.y, b.y, x);
    result.z = smoothstep(a.z, b.z, x);
    return result;
}


//Box - exact   (Youtube Tutorial with derivation: https://www.youtube.com/watch?v=62-pRVZuS5c)

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float nGon(in int n, in vec2 p, in float r) {
    // these 2 lines can be precomputed
    float an = 6.2831853 / float(n);
    float he = r * tan(0.5 * an);

    // rotate to first sector
    p = -p.yx; // if you want the corner to be up
    float bn = an * floor((atan(p.y, p.x) + 0.5 * an) / an);
    vec2 cs = vec2(cos(bn), sin(bn));
    p = mat2(cs.x, -cs.y, cs.y, cs.x) * p;

    // side of polygon
    return length(p - vec2(r, clamp(p.y, -he, he))) * sign(p.x-r);
}
float toPrism(in float d2d, in float v, in float size) {
    vec2 d = vec2(d2d, abs(v) - 0.5 * size);
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
float nPrism(in int n, in vec3 p, in float r, in float depth) {
    float d = nGon(n, p.xy, r);
    return toPrism(d, p.z, depth);
}
