// https://www.reddit.com/r/C_Programming/comments/1g2bixq/why_are_cossin_functions_so_slow/
void mainImage( out vec4 fc, in vec2 st )
{
    vec2 uv = st/iResolution.xy;
    float t = iTime;

    //Uint8 r = (Uint8)((0.5 + 0.5 * cos((t + x_normalized + 0.0))) * 255);
    //Uint8 g = (Uint8)((0.5 + 0.5 * cos((t + x_normalized + 2.0))) * 255);
    //Uint8 b = (Uint8)((0.5 + 0.5 * cos((t + x_normalized + 4.0))) * 255);
    //Uint8 a = 255;
    vec3 col;
    col.r = 0.5 + 0.5*cos((t + uv.x + 0.0));
    col.g = 0.5 + 0.5*cos((t + uv.x + 2.0));
    col.b = 0.5 + 0.5*cos((t + uv.x + 4.0));

    // Output to screen
    fc = vec4(col,1.0);
}
