int quad_spiral(ivec2 t)
{
    t++; // center tatami square
    int n = 2*max(abs(t.x),abs(t.y)); // diameter
    //return n*n + ((abs(t.x) > abs(t.y) ? t.x < 0 : t.y < 0)?+1:-1)*(n + t.x - t.y); // square spiral id
    //return (n*n + ((abs(t.x) > abs(t.y) ? t.x < 0 : t.y < 0)?+1:-1)*(n + t.x - t.y)+0)>>1; // simple tatami square id (pre-increment coordinates to center)
    return (n*n + ((abs(t.x) > abs(t.y) ? t.x < 0 : t.y < 0)?+1:-1)*(n + t.x - t.y)+1)>>1; // tatami spiral id (with central 1x1 tile)
}

bool edge(vec2 I)
{
    float e = 0.1;
    int a = quad_spiral(ivec2(floor(I-e)));
    int b = quad_spiral(ivec2(floor(I+e)));
    return a != b;
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = (I + I - R) / R.y;
    vec3 c = vec3(0);
    if(all(lessThan(abs(I), vec2(1))))
    {
        I *= 9.0;
        ivec2 t = ivec2(floor(I));
        int id = quad_spiral(t);
        if(true) // color variant
        {
            c = mix(vec3(1,0.75,0),vec3(0,0.75,0),float(id)/220.0);
            if(((id - int(floor(iTime*5.0))) & 63) == 0) c = vec3(0,0.5,1);
        }
        else // gloomy, high contrast
        {
            c = vec3(id)/220.0;
            if(((id + int(floor(iTime*5.0))) & 63) == 0) c = vec3(1,0,0);
        }
        if(edge(I)) c *= 0.0;
    }

    // NOTE: deliberately omitting gamma here - looks better, here
    o = vec4(c, 1.0);
}
