// For correct ambient occlusion sample 2x2x2 voxels (slow!)
//#define HIGH_QUALITY XXX currently broken XXX


#define TERRAIN_ITERATIONS 5


#define EPSILON 0.001
#define DIST_MAX 100.0
#define ITER_MAX 100u


float hash12(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash13(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float box(vec3 p, vec3 s) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec3 d = abs(p) - s;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float box(vec3 p, vec3 p0, vec3 p1)
{
    return box(p-(p1+p0)/2.0, abs(p1-p0)/2.0);
}

float box(vec3 p, vec3 p0, vec3 p1, float r)
{
    r = max(r,0.0);
    return box(p-(p1+p0)/2.0, abs(p1-p0)/2.0-r)-r;
}

float cylinder(vec3 p, vec2 s) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - s;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float frame(vec3 p, vec3 b, float e) // https://iquilezles.org/articles/distfunctions/
{
        p = abs(p  )-b;
    vec3 q = abs(p+e)-e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float frame(vec3 p, vec3 p0, vec3 p1, float e)
{
    return frame(p-(p1+p0)/2.0,abs(p1-p0)/2.0,e);
}

float torus(vec3 p, vec2 r)
{
    vec2 q = vec2(length(p.xy)-r.x,p.z);
    return length(q)-r.y;
}

float ortho_circle_flower_sdf(int n, vec2 p) // https://www.shadertoy.com/view/dldBWl orthogonal circle flower sdf, 2023 jt
{
    n = 2*n+1; // n must be odd
    float slice = pi/float(n);

    p.x = abs(p.x); // mirror horizontally
    float r = (p.y*p.y+2.0*p.y+p.x*p.x+1.0)/(2.0*p.x); // calculate radius of circular arc leaf passing through this point

    // equidistant circle segment endpoints on unit circle
    float r0 = tan(floor(atan(r)/slice)*slice); // to angle then quantize then to length
    float r1 = tan( ceil(atan(r)/slice)*slice); // to angle then quantize then to length

    // circle center
    vec2 c0 = vec2(r0,-1); // lower leaf
    vec2 c1 = vec2(r1,-1); // upper leaf

    // endpoints
    float mu = atan(p.x,p.y);
    mu = (floor(mu/slice/2.0)+0.5)*slice*2.0;
    vec2 c = vec2(sin(mu),cos(mu));

    return
        //length(p) < 1.0; // approximate
        dot(p, c) < 1.0 // thanks to tdhooper for fixing remaining discontinuities by changing length(p) < 1.0 to dot(p, c) < 1.0 (turns circle into polygon)
        ?
        min(abs(length(p-c0)-r0),abs(length(p-c1)-r1)) // circular arc leafs
        :
        length(p-c); // leaf endpoints
}

float ortho_circle_flower_sdf(int m, int n, vec3 p) // https://www.shadertoy.com/view/dtGBDz orthogonal circles grassy plant (jt)
{
    float phi = round(atan(p.y,p.x)/(2.0*pi/float(m)))*(2.0*pi/float(m)); // polar & quantize
    p.xy = mat2(cos(phi),-sin(phi),sin(phi),cos(phi))*p.xy;
    return length(vec2(ortho_circle_flower_sdf(n, vec2(p.xz)),p.y));
}

vec4 random_unit_quaternion(vec3 r) // r in [0,1] https://stackoverflow.com/questions/31600717/how-to-generate-a-random-quaternion-quickly
{
    return
        vec4
        (
            sqrt(1.0-r.x) * sin(2.0*pi*r.y),
            sqrt(1.0-r.x) * cos(2.0*pi*r.y),
            sqrt(    r.x) * sin(2.0*pi*r.z),
            sqrt(    r.x) * cos(2.0*pi*r.z)
        );
}

// https://en.wikipedia.org/wiki/Quaternions_and_spatial_rotation#Quaternion-derived_rotation_matrix
mat3 quaternion_to_matrix(vec4 q) // https://www.euclideanspace.com/maths/geometry/rotations/conversions/quaternionToMatrix/index.htm
{
    float s = dot(q,q); // just in case, not needed for uniform quaternions
    return
        /*
        mat3
        (
            vec3(1.0-2.0*(q.y*q.y+q.z*q.z),       2.0*(q.x*q.y-q.z*q.w),       2.0*(q.x*q.z+q.y*q.w)),
            vec3(    2.0*(q.x*q.y+q.z*q.w), 1.0 - 2.0*(q.x*q.x+q.z*q.z),       2.0*(q.y*q.z-q.x*q.w)),
            vec3(    2.0*(q.x*q.z-q.y*q.w),       2.0*(q.y*q.z+q.x*q.w), 1.0 - 2.0*(q.x*q.x+q.y*q.y))
        );
        */
        mat3(1) // identity
        +
        2.0
        *
        mat3 // could swizzle with cross-product be used to compactify this?
        (
            vec3(-q.y*q.y-q.z*q.z,+q.x*q.y-q.z*q.w,+q.x*q.z+q.y*q.w),
            vec3(+q.x*q.y+q.z*q.w,-q.x*q.x-q.z*q.z,+q.y*q.z-q.x*q.w),
            vec3(+q.x*q.z-q.y*q.w,+q.y*q.z+q.x*q.w,-q.x*q.x-q.y*q.y)
        )
        /
        s;
}

mat3 random_rotation(vec3 r)
{
    return quaternion_to_matrix(random_unit_quaternion(r));
}

float terrain(int n, ivec2 v) // https://www.shadertoy.com/view/43syR7 voxel terrain (jt)
{
    int m = 1 << n;
    ivec2 v0 = v & ~(m-1); // clear n lower bits
    v -= v0; // n lower bits

    float h00 = hash12(vec2(v0 + ivec2(0,0)));
    float h01 = hash12(vec2(v0 + ivec2(m,0)));
    float h10 = hash12(vec2(v0 + ivec2(0,m)));
    float h11 = hash12(vec2(v0 + ivec2(m,m)));

    //return hash12(vec2(v0));
    //return (float(m-v)*h00+float(m)*h01)/float(m);
    //return mix(h00,h01,float(v)/float(m));
    vec2 q = vec2(v)/vec2(m-1);
    return mix(mix(h00,h01,q.x),mix(h10,h11,q.x),q.y);
}

float terrain_iterated(int n, ivec2 v) // https://www.shadertoy.com/view/43syR7 voxel terrain (jt)
{
    float h = 0.0;
    for(int i = 1; i < n; i++)
    {
        h += terrain(i, v) * float(1<<i);
    }
    return h;
}

struct result
{
    vec3 color;
    float dist;
};

result map(ivec3 v, vec3 l) // unit grid: voxel = float(position), local = fract(position)
{
    float h = terrain_iterated(TERRAIN_ITERATIONS,v.xy);

    if(abs(length(vec2(v)) - 50.0) < 5.0) h = 0.0; // cut ring for camera
    //if(abs(length(vec3(v)-vec3(0,0,20)) - 50.0) < 5.0) h = 0.0; // cut sphere for camera
    //if(torus(vec3(v-ivec3(0,0,20)), vec2(50,5)) < 0.0) h = 0.0; // cut ring for camera

    if(v.z <= int(floor(h))+1)
    {
        float grey = hash13(vec3(v)*123.456);
        vec3 color = hash33(vec3(v)*123.456);

        vec3 r = hash33(vec3(v)*123.456);

        // TODO: uncorrelate rotation from colors
        mat3 R = random_rotation(r.xyz); // NOTE: r must be in [0,1]

        //if(v.z < int(floor(h))) return result(vec3(1.0), box(R*(l-0.5), vec3(0.49))-0.02);
        if(v.z < int(floor(h))) return result(vec3(mix(0.6,1.0,grey)), box(R*(l-0.5), vec3(0.49))-0.02);
        //if(v.z == int(floor(h))) return result(vec3(0.1), box(R*(l-0.5), vec3(0.49))-0.02);
        if(v.z == int(floor(h))) return result(vec3(mix(0.1,0.2,grey)), box(R*(l-0.5), vec3(0.49))-0.02);
        if(v.z == int(floor(h))+1)
        {
            vec3 r = hash33(vec3(v)*123.456);
            float s = mix(0.5,1.0,r.z);
            return result(vec3(color), ortho_circle_flower_sdf(5, 5, (l-0.5)*2.0/s-vec3(0,0,-0.5/s))/2.0*s-0.01);
        }
    }

    return result(vec3(0), DIST_MAX); // signal to skip this voxel
}
#ifdef HIGH_QUALITY
result map(vec3 p) // for correct ambient occlusion sample 2x2x2 voxels (slow!)
{
    // I think kastorp originally suggested to sample only 2x2x2 instead of 3x3x3, thanks!
    result d = result(vec3(0), DIST_MAX);
    ivec3 s = ivec3(step(0.5, fract(p)));
    ivec3 o;
    for(o.z = s.z-1; o.z < s.z+1; o.z++)
        for(o.y = s.y-1; o.y < s.y+1; o.y++)
            for(o.x = s.x-1; o.x < s.x+1; o.x++)
            {
                result r = map(ivec3(floor(p))+o, fract(p)-vec3(o));
                if(r.dist < d.dist)
                    d = r;
            }
    return d;
}
#else
result map(vec3 p)
{
    return map(ivec3(floor(p)), fract(p));
}
#endif
// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize(k.xyy*map(p + k.xyy*h).dist +
                     k.yyx*map(p + k.yyx*h).dist +
                     k.yxy*map(p + k.yxy*h).dist +
                     k.xxx*map(p + k.xxx*h).dist);
}

// NOTE: Apparently sign fails on some systems! Thanks to spalmer for debugging this!
vec3 sgn(vec3 v) // WORKAROUND FOR COMPILER ERROR on some systems
{
    return step(vec3(0), v) * 2.0 - 1.0;
}

result trace(vec3 ro, vec3 rd, float t0, float t1) // ray-march sdf handling discontinuities between voxels  (jt)
{
    result h;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++) // finite loop originally suggested by pyBlob to avoid stalling if ray parallel to surface just above EPSILON
    {
        vec3 p = ro + rd * t;
        h = map(p);
        if(h.dist < EPSILON)
            return result(h.color, t);

        // NOTE: An extra step per voxel, use if sdf discontinuous between voxels
        //       Could make this conditional by prefixing sth. like if(voxel_changed) // suggested by spalmer, see variant below
        // constrain step to voxels (voxel-snap ray-march plugin by jt, thanks to Shane for the idea!)
        {
            // NOTE: assuming unit grid
            // sgn(rd)*0.5 are the walls in ray direction, fract(p) - 0.5 is center of voxel
            vec3 sd = (sgn(rd)*0.5 - (fract(p) - 0.5))/rd; // distances to voxel sides / walls
            vec3 n = step(sd.xyz, min(sd.yzx, sd.zxy)); // component true if corresponding wall is nearest (at most one component true) NOTE: originally I used lessThanEqual, min from fb39ca4/kzy then switched to step, min by iq
            float skip = dot(sd, vec3(n)) + EPSILON; // distance to next voxel: sum up all components, weighted by the nearest flag (assuming only one component is true this selects the nearest component)
            h.dist = min(h.dist, skip); // constrain step to at most next block to handle sdf discontinuities between voxels
        }

        t += h.dist;
    }

    //return result(h.color, t); // stop on running out of iterations
    return result(h.color, t1); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
//       Thanks to spalmer for pointing that out.
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).dist < t1 ? 0.0 : 1.0;
}
#ifdef HIGH_QUALITY
// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).dist;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}
#endif
// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).dist;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec2 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi));
}

float checker(vec3 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi)*sin(p.z*pi));
}

vec3 material(vec3 p)
{
    return vec3(1.0); // plain
    //return mix(vec3(0.25), vec3(0.75), checker(p*2.0+0.5)); // checker
}

void mainImage(out vec4 fragColor, vec2 I)
{
    float t = iTime/100.0;

    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (I + I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    //float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    //float pitch = pi*2.0/3.0 - pi / 3.0 * float(iMouse.y) / float(R.y);
    float pitch = pi*3.5/6.0 - pi / 6.0 * float(iMouse.y) / float(R.y);
    //float pitch = pi - pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(t);
    //pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;
    pitch = !demo ? pitch : pi / 2.0;

    //vec3 ro = vec3(0.0, 0.0,-7.5);
    vec3 ro = vec3(0.0, 0.0,-50.0);
    //vec3 ro = vec3(0.0, 0.0,0.0);
    vec3 rd = normalize(vec3(I.xy, 2.0));

    if(true) // circle
    {
        mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
        ro = M * ro;
        rd = M * rd;
    }
    else // canyon flythrough
    {
        ro = yaw_pitch_roll(yaw, pitch, 0.0) * ro;
        rd = yaw_pitch_roll(yaw - pi/2.0, pitch, 0.0) * rd;
    }
    //ro.z += 5.0;
    ro.z += 20.0;
    //ro.z += terrain_iterated(TERRAIN_ITERATIONS,ivec2(floor(ro.xy))); // "walk" on terrain

    //vec3 sky_color = mix(vec3(0,0.5,0.5),vec3(0,0,1),abs(-rd.z));
    vec3 sky_color = mix(vec3(0,0.25,0.5),vec3(0,0,1),abs(-rd.z));
    vec3 color = vec3(1);
    result r = trace(ro, rd, 0.0, DIST_MAX);
    if(r.dist < DIST_MAX)
    {
        color *= r.color;
        vec3 dst = ro + rd * r.dist;
        vec3 n = normal(dst);

        //color *= (n * 0.5 + 0.5);
        color *= material(dst);

        //vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
        //vec3 lightdir = normalize(vec3(1.0, 2.0, 3.0));
        vec3 lightdir = normalize(mix(vec3(1.0, 1.0, 1.0),vec3(1.0, 2.0, 3.0),0.5+0.5*cos(tau*t/2.0))); // oscillate slowly between afternoon and noon
        //vec3 lightdir = normalize(vec3(cos(tau*t),sin(tau*t),0.5+0.5*cos(tau*t)));

        vec3 ambient = vec3(0.1);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0); // requires HIGH_QUALITY
        color *= (ambient * calcAO(dst, n) + brightness);

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        //vec3 fog_color = vec3(0.2);
        vec3 fog_color = sky_color;
        //color = mix(fog_color, vec3(color), exp(-pow(r.dist/40.0, 2.0))); // fog
        color = mix(fog_color, vec3(color), exp(-pow((1.0-rd.z*rd.z)*r.dist/40.0, 2.0))); // fog but not below
    }
    else
    {
        color *= sky_color;
    }

    color = tanh(color); // roll-off overly bright colors
    fragColor = vec4(color, 1);
    fragColor = sqrt(fragColor); // approximate gamma
}
