float euclidien_divide(float a, float b) {
    return a - floor(a/b)*b;
}

float F(float t, float x, float y) {
    return x*(3.*(1.-x/euclidien_divide(iTime,100.0))-2.*y);
}

float G(float t, float x, float y){
    return y*(-4.0+x);
}

vec3 euler(float t, float dt, vec2 origine) {
    float x = origine.x;
    float y = origine.y;

    for (float i = 0.0; i<t; i+=dt) {
        float x_1 = x + dt*F(i,x,y);
        float y_1 = y + dt*G(i,x,y);
        x = x_1;
        y = y_1;
    }
    return vec3(x,y,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.x;
    float t = 10.0; // iMouse.x/iResolution.x*20.0;
    vec3 col = euler(t,0.001,uv*250.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
