// #extension GL_EXT_gpu_shader4 : enable

// I got this rand function from here: https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl#4275343
float rand(vec2 co){
    return fract(sin(dot(vec2(co.x+round(iTime), co.y-round(iTime)), vec2(12.9898, 78.233))) * 43758.5453);
}

float new_rand_offset(float val) {
    return rand(vec2((round(iTime))/val, 0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy;
  // uv.y = 1.0 - uv.y;
  int index = int(fragCoord.y / 15.0);
  uv.x += min(max(new_rand_offset(float(-index)), .4), .6) - .5;
  vec4 col = vec4(0.0);
  col.a = texture(iChannel0, uv).a;
  col.g = texture(iChannel0, uv).g;
  col.r = texture(iChannel0, uv).r;
  col.b = texture(iChannel0, uv).b;
  fragColor = col;
}
