void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    // Green screen
    vec3 img = texture(iChannel0, uv + sin(uv * 10. + iTime) * 0.1).rgb;

    float refBlur = max(img.r, img.b) * 0.45;
    float amaskBlur = img.g - refBlur;
    amaskBlur = 1.0 - smoothstep(0.1, 0.2, amaskBlur);

    vec3 img2 = texture(iChannel0, uv).rgb;

    float ref = max(img2.r, img2.b) * 1.1;
    float amask = img2.g - ref;
    amask = 1.0 - smoothstep(0.1, 0.2, amask);

    col = vec3(amask);


    col = vec3(amask, amaskBlur, 0.);

    #define C1 vec3(0.580,1.000,0.651)
    #define C2 vec3(1.000,0.482,0.000)
    #define C3 vec3(0.404,0.325,0.455)

    vec3 c1 = mix(C1, C2, amask);
    vec3 c2 = mix(C1, C3, amask);
    vec3 c3 = mix(c1, c2, amaskBlur);

    col = c3;

    // Output to screen
    fragColor = vec4(col,1.0);
}
