vec3 palette (float t)
{
    vec3 a = vec3(0.598,0.540,0.928);
    vec3 b = vec3(-0.362,0.355,-0.456);
    vec3 c = vec3(1.108,1.108,-1.33);
    vec3 d = vec3(0.2,0.138,-1.252);
    return a + b*cos( 6.28318*(c*t+d));
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy * 2.0 ) - 1.0;

    uv.x *= iResolution.x/iResolution.y;

    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

for (float i = 0.0; i < 2.0; i++)
{    
     uv = fract(uv*1.3) - 0.5;

     float d = length(uv) * exp(length(uv0*0.2));

     d = abs(d);

     vec3 col = palette(length(uv0*1.5)+ i*0.5 +iTime*2.0); 

     d = fract(sin(d* 1.5 + iTime* 0.3));  

     float x = fract(sin(d* 2.0+ iTime* 0.1));

     d = smoothstep(0.0,1.0,d);

     d *= x/2.0;


     d = pow(0.005/d,1.2);

    finalColor += col * d;
}

    fragColor = vec4(finalColor,1.0);
}
