void mainImage(out vec4 C, vec2 O)
{
    C = vec4(1);

    // pattern pixel width: multiple of 4
    O /= 40.;

    O = fract(O - .5);
    O = abs(O * 4. - 2.);

    float f;

    float pattern = abs(iMouse).x / iResolution.x * 4.;
    if (pattern < 1.)
        f = min(O.x, O.y); // grid
    else if (pattern < 2.)
        f = O.x; // vertical
    else if (pattern < 3.)
        f = O.y; // horizontal
    else
        f = 0.; // white

    if (abs(iMouse).y / iResolution.y * 2. >= 1.)
        f = 2. - f;

    C.xyz = vec3(f < 1.);
}
