#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001

float getPattern(vec3 p) {
    float r = length(p.xz);
    float a = atan(p.z, p.x) + r + iTime * 0.5;
    return sin(a * 24.0);
}

float map(vec3 p) {
    float pattern = getPattern(p);
    float disc = length(p) - 2.0;
    return max(abs(p.y) - 0.1, max(disc, -pattern * 0.1));
}

float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = map(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }
    return dO;
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0);
    vec3 n = map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
    );
    return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera movement
    float t = iTime * 0.5;
    float radius = 5.0 + sin(t * 0.5) * 2.0;
    float height = 3.0 + cos(t * 0.7) * 1.5;
    vec3 ro = vec3(cos(t) * radius, height, sin(t) * radius);
    vec3 lookAt = vec3(0, 0, 0);

    vec3 f = normalize(lookAt - ro);
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(f + uv.x*r + uv.y*u);

    float d = rayMarch(ro, rd);
    vec3 p = ro + rd * d;

    vec3 col = vec3(0);
    if(d < MAX_DIST) {
        vec3 n = getNormal(p);
        float pattern = getPattern(p);
        col = vec3(smoothstep(0.0, 1.0, pattern * 0.5 + 0.5));
        col /= (1.0 + length(p) * 0.5);  // Fade out with distance
    }

    fragColor = vec4(col, 1.0);
}
