void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float clipBegin = mod(iTime / 5.0, 1.2) - 0.2;
    float clipEnd = clipBegin + 0.2;

    if(uv.y > clipBegin && uv.y < clipEnd) 
    {
        uv.y -= clipBegin;
        uv.y /= clipEnd - clipBegin;
        fragColor = texture(iChannel1, uv);
    }
    else 
    {
        if(uv.y < clipBegin)
        {
            uv.y += clipEnd - clipBegin;
        }

        fragColor = texture(iChannel0, uv);
    }
}
