void mainImage( out vec4 O, in vec2 F )
{
    O = vec4(0);
    O.g = smoothstep(.5,1.,pow(texture(iChannel0,.2*F/iResolution.xy).r,.4))*(1.+sin(3.1416*F.y*.5)*.8);
}

void mainImage( out vec4 O, in vec2 F )
{
    if (iFrame<1 && texture(iChannel0,F/1024.).r>.5) O.r=1.;
    else
    {
        vec2 i = 1./iResolution.xy;
        F*=i;
        if (iMouse.z>0. && length(F-iMouse.xy*i*.2)<length(i)) O=1.-texture(iChannel1,F);
        else
        {
            float n = texture(iChannel1,F+vec2(i.x,0)).r+
            texture(iChannel1,F-vec2(i.x,0)).r+
            texture(iChannel1,F+vec2(0,i.y)).r+
            texture(iChannel1,F-vec2(0,i.y)).r+
            texture(iChannel1,F+i).r+
            texture(iChannel1,F-i).r+
            texture(iChannel1,F+vec2(-i.x,i.y)).r+
            texture(iChannel1,F+vec2(i.x,-i.y)).r;
            if (texture(iChannel1,F).r>.5)
            {
                if (n<2.) O.r=0.;
                else if (n>3.) O.r=0.;
                else if (n<4.) O.r=1.;
            }
            else
            {
                if (n==3.) O.r=1.;
                else O.r=0.;
            }
        }
    }
}
