void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime * 0.1;

    vec3 color = vec3(0.0);
    for (float i = 0.0; i < 1.0; i += 0.1)
    {
        float t = time * (1.5 - i) + i * 5.0;
        float y = sin(uv.x * 10.0 + t) * 0.05 + 0.05;
        y += cos(uv.x * 15.0 - t * 1.5) * 0.03;
        y += sin(uv.x * 20.0 + t * 1.3) * 0.02;

        float glow = 1.0 / (abs(y - uv.y) * 50.0 + 1.0);

        color.r += glow * (0.5 + 0.5 * sin(uv.x * 40.0 - t * 2.0));
        color.g += glow * (0.5 + 0.5 * sin(uv.x * 30.0 + t));
        color.b += glow * (0.5 + 0.5 * cos(uv.x * 20.0 - t * 1.5));
    }

    fragColor = vec4(color, 1.0);
}
