void mainImage(out vec4 O, in vec2 U)
{O=texture(iChannel0, U.xy/iResolution.xy);}

//#define DRAW

#define MU (.05 + 0.1 * cos(iTime * .05))
#define NU (.5 + 0.5 * sin(iTime * .1))
#define RHO 10.0

#define _OP0(a, b, c) (dot(a - sin(b) , c - cos(b)) * MU + dot(a - cos(c), b - sin(c)) * NU)
#define _OP1(a, b, c) (pow(b+a, 2.)*dot(a - sin(b), cos(b)) * MU + c*dot(a - cos(c), b - sin(c)) * NU)
#define _OP2(a, b, c) (sin(a + exp(-b)) * cos(c * log(1.0 + abs(a))) * MU + cos(b - tanh(c)) * sin(a * exp(-c)) * NU)
#define _OP3(a, b, c) (sin(a * pow(b, 2.0) + tan(c)) * MU + cos(pow(a, 3.0) - sin(b) * cos(c)) * NU)
#define _OP4(a, b, c) (sinh(a * log(1.0 + abs(b))) * cosh(c * exp(-b) * MU) + tanh(pow(b, 2.0) - log(1.0 + abs(c))) * cos(a * exp(c)) * NU)
#define _OP5(a, b, c) (pow(exp(sin(a) * cos(b)), 2.0) * log(1.0 + abs(c)) * MU + tanh(c * pow(a - b, 3.0)) * NU)
#define _OP6(a, b, c) (sin(a * tanh(log(1.0 + abs(b)))) * cos(pow(c, 2.0) * exp(-a)) * MU + sinh(b * log(1.0 + abs(a)) * tan(c)) * NU)
#define _OP7(a, b, c) (cosh(a * pow(b, 2.0) + log(1.0 + abs(c))) * sinh(b - exp(c)) * MU + tanh(pow(a * exp(-b), 3.0) * c) * NU)

//#define OP _OP0
#define OP(a, b, c) (_OP0(a, b, c) * _OP7(a, b, c) - _OP1(a, b, c) * _OP5(a,b,c))


void mainImage(out vec4 O, in vec2 U) {
    vec2 uv = U / iResolution.xy;
    vec4 col = texelFetch(iChannel0, ivec2(U.xy), 0);

    float r = col.x, g = col.y, b = col.z;

    col += vec4(OP(r, g, b), OP(g, b, r), OP(b, r, g), 0.0);
    col += lap(col, U, iChannel0, R) * RHO;

    if (sign(iMouse.z) == 1.0 || iFrame == 0) {
        #ifdef DRAW
        col += SOURCE(uv, iMouse.xy / iResolution.xy, 0.01, .1 * vec4(abs(cos(-sin(iTime))), abs(cos(iTime)), abs(sin(iTime)), 0.0));
        #else
        col = vec4(.0000001);
        vec4 I = vec4(.0001, col.xyz);
        col += SOURCE(uv, vec2(.33, .33), SRC_SIZE, I.xyyy);
        col += SOURCE(uv, vec2(.50, .75), SRC_SIZE, I.yxyy);
        col += SOURCE(uv, vec2(.66, .33), SRC_SIZE, I.yyxy);
        #endif
    }

    O = normalize(col);
}

//#define MU 0.00389
//#define NU 0.09086
//#define MU 0.00838
//#define NU 0.12159
//#define MU 0.2
//#define NU 0.81
//#define MU .15
//#define NU .75

#define R iResolution.xy
#define SRC_SIZE .0001
#define SOURCE(p, o, r, col) col * smoothstep(r+.1, r-.5, length(o - p))


vec4 lap(in vec4 col, in vec2 p, in sampler2D tex, in vec2 res) {
    vec4 sum = -4.0 * col;
    sum += texelFetch(tex, ivec2(mod(p + vec2(1., 0.),res)), 0);
    sum += texelFetch(tex, ivec2(mod(p - vec2(1., 0.),res)), 0);
    sum += texelFetch(tex, ivec2(mod(p + vec2(0.0, 1.),res)), 0);
    sum += texelFetch(tex, ivec2(mod(p - vec2(0.0, 1.), res)), 0);
    return sum;
}
