float rect(in vec2 st, in vec2 size) {
    size = 0.5 - size * 0.5;
    vec2 uv = step(size, st * (1.0 - st));
    return uv.x * uv.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord / iResolution.xy;


    vec2 rectPos = vec2(0.0, 0.6); 

    vec2 rectSize = vec2(0.4, 0.4);

    vec2 st = (uv - rectPos) / rectSize;

    float inRect = rect(st, vec2(1.0));

    vec3 rectColor = st.y > 0.5 ? vec3(0.0) : vec3(1.0);

    vec3 bgColor = vec3(0.61,0.01,0.01);

    vec3 col = mix(bgColor, rectColor, inRect);

    fragColor = vec4(col, 1.0);
}
