#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h + vec3(85,12,2)   )*.6 + .5 )

#define H2(h)  (  cos(  h*h/3. + vec3(1,1,2)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, R.y, R.y*2.))*80.;

    float sc,dotp,totdist=0., t=.7, tt=iTime/4.;

    for (float i=0.; i<200.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(0,0,-1.7);
        p.xz *= rot(tt/2.);

        p.yzw = ROT(p.xyz, normalize( vec3(sin(t/2.), sin(t), cos(t/3.) )  ),t);

        sc = 1.1; 

        p.zw *= rot(tt);


        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p)*.77;

            dotp = max(1./dot(w,w),.1);

            sc *= dotp; 

            p = p * dotp - .42 ;

            w = .75*p - .025 ;


        }

        float dist = abs( length(p)-.025)/sc ;  //funky distance estimate
        float stepsize = dist/15. ;     
        totdist += stepsize;              //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
             3e-3* H2(  atan( p.w, p.z )) + 
             + mix( vec3(1), H(M(sc)),.95) *.03*  exp(-i*i*stepsize*stepsize*8e3);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
